// Se no houver token, retorna session undefined
if (!msg.token) {
    msg.session = undefined;
    return msg;
}

// Verifica se o servidor tem os detalhes do token
var details = flow.get(msg.token);
if (!details) {
    msg.session = undefined;
} else {
    // Detalhes da sesso
    msg.session = {
        'uniqueid': details.session.uniqueid,
        'start_Time': undefined,
        'Utilizador_ID': details.user.Utilizador_ID,
        'Utilizador_isActive': details.user.Utilizador_isActive,
        'Utilizador_Nome': details.user.Utilizador_Nome,
        'Utilizador_Email': details.user.Utilizador_Email,
        'TipoUtilizador_ID': details.user.TipoUtilizador_ID,
        'Grupos': details.user.Grupos,
        'Utilizador_image': details.user.Utilizador_image
    };

    // Detalhes do usurio
    msg.payload = {
        'Utilizador_ID': details.user.Utilizador_ID,
        'Utilizador_isActive': details.user.Utilizador_isActive,
        'Utilizador_Nome': details.user.Utilizador_Nome,
        'Utilizador_Email': details.user.Utilizador_Email,
        'TipoUtilizador_ID': details.user.TipoUtilizador_ID,
        'Grupos': details.user.Grupos,
        'Utilizador_image': details.user.Utilizador_image
    };
}

return msg;

